import React from 'react'
import { useQuery } from '@apollo/client'
import { ALL_BOOKS } from '../query'

const Books = (props) => {

  const books = useQuery(ALL_BOOKS)

  if (!props.show) {
    return null
  }

  if(books.loading){
    return (
      <div>loading...</div>
    )
  }

  let genres = new Set([books.data.allBooks.map(x => x.genres)])
  genres = [...genres].flat()

  return (
    <div>
      <h2>books</h2>

      <table>
        <tbody>
          <tr>
            <th></th>
            <th>
              author
            </th>
            <th>
              published
            </th>
          </tr>
          {books.data.allBooks.map(a =>
            <tr key={a.title}>
              <td>{a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          )}
          {genres.map(x =>
          <button onClick={console.log(x)}>{x}</button>
            )}
        </tbody>
      </table>
    </div>
  )
}

export default Books
