import React, { useEffect, useState } from 'react'
import { useLazyQuery, useQuery } from '@apollo/client'
import { ALL_BOOKS, BOOKS_OF_GENRE, ME } from '../query'

const Recommend = (props) => {

  const books = useQuery(ALL_BOOKS)
  const [genreBooks, result] = useLazyQuery(ALL_BOOKS)
  const [someBooks, setBooks] = useState([])
  const user = useQuery(ME)

  useEffect(() => {
    if(user.data) {
      console.log(result.data)
      genreBooks({ variables: {genre: user.data.me.favoriteGenre}})
    }
  }, [genreBooks, user])

  useEffect(() => {
    if(result.data && result.data.allBooks){
      console.log('Give result:', result)
      setBooks(result.data.allBooks)
    }
  }, [result.data, someBooks])

  console.log('result?', result)

  if(!props.show){
    return null
  }

  if(books.loading || user.loading){
    return (
      <div>
        loading...
      </div>
    )
  }

  const favGenre = user.data.me.favoriteGenre
  const favBooks = books.data.allBooks.filter(x => x.genres.includes(favGenre))
  
  return (
    <div>
      <h2>Recommendations</h2>

      <p>books in your favorite genre <b>{favGenre}</b></p>
      <table>
        <tbody>
          <tr>
            <th></th>
            <th>
              author
            </th>
            <th>
              published
            </th>
          </tr>
          {favBooks.map(a =>
            <tr key={a.title}>
              <td>{a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          )}
        </tbody>
      </table>

    </div>
  )
}

export default Recommend
