import { gql } from '@apollo/client'

export const ALL_AUTHORS = gql`
  query {
    allAuthors {
      name
      born
      bookCount
    }
  }
`
// NOTE! Authors does not work yet and causes a crash
export const ALL_BOOKS = gql`
  query {
    allBooks {
      title
      published
      author
    }
  }
` 
export const ADD_BOOK = gql`
  mutation createBook($title: String!, $published: Int!, $genres: [String!], $name: String!, $born: Int) {
    addBook(
      title: $title
      published: $published
      genres: $genres
      name: $name
      born: $born
    ) {
      title
      published
    }
  }
`

export const EDIT_BIRTH = gql`
  mutation editBirth($name: String!, $setBornTo: Int!) {
    editAuthor(
      name: $name
      setBornTo: $setBornTo
    ) {
      name
      born
    }
  }
`
